package com.aayushpuranik.todolist.data.dbimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport androidx.room.Updateimport com.aayushpuranik.todolist.domain.model.Person@Daointerface RegistrationDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insert(person: Person)    @Query("SELECT * FROM Person")    suspend fun getPersons(): Person?    @Update    suspend fun updatePerson(person: Person)}