package com.aayushpuranik.todolist.data.repositoryimport com.aayushpuranik.todolist.data.db.RegistrationDatabaseimport com.aayushpuranik.todolist.domain.model.Personimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport kotlinx.coroutines.withContextimport javax.inject.Injectclass RegistrationRepository @Inject constructor(val registrationDatabase: RegistrationDatabase) {    suspend fun savePersonDetails(person: Person) {        registrationDatabase.registrationDao().insert(person)    }    suspend fun getPersonFromDB(): Person? {        return registrationDatabase.registrationDao().getPersons()    }}