package com.aayushpuranik.todolist.diimport android.content.Contextimport androidx.room.Roomimport com.aayushpuranik.todolist.data.db.RegistrationDatabaseimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.qualifiers.ApplicationContextimport dagger.hilt.components.SingletonComponentimport javax.inject.Singleton@InstallIn(SingletonComponent::class)@Moduleclass DatabaseModule {    @Singleton    @Provides    fun provideDB(@ApplicationContext context: Context): RegistrationDatabase {        return Room.databaseBuilder(context, RegistrationDatabase::class.java, "RegistrationDB")            .build()    }}