package com.aayushpuranik.todolist.domain.useCaseimport com.aayushpuranik.todolist.data.repository.RegistrationRepositoryimport com.aayushpuranik.todolist.domain.model.Personimport javax.inject.Injectclass RegistrationUserCase @Inject constructor(    private val registrationRepository: RegistrationRepository) {    fun execute(person: Person): Result<Unit> {        try {            registrationRepository.savePersonDetails(person)            return Result.success(Unit)        } catch (ex: Exception) {            return Result.failure(ex)        }    }}