package com.aayushpuranik.todolist.domain.useCaseimport com.aayushpuranik.todolist.data.repository.RegistrationRepositoryimport com.aayushpuranik.todolist.domain.model.Personimport com.aayushpuranik.todolist.presentation.UIStateimport javax.inject.Injectclass RegistrationUserCase @Inject constructor(    private val registrationRepository: RegistrationRepository) {    suspend fun execute(person: Person): UIState<Person?> {        registrationRepository.savePersonDetails(person = person)        return UIState.Success(registrationRepository.getPersonFromDB())    }}