package com.aayushpuranik.todolist.domain.useCaseclass ValidateConfirmPassword {    fun execute(password: String?, confirmPassword: String?) : ValidateResult {        if(confirmPassword?.trim().isNullOrBlank()) {            return ValidateResult(false, "Confirm Password is needed")        } else {            if(password != confirmPassword) {                return ValidateResult(false, "password and confirm password need to be same")            } else {                return ValidateResult(true)            }        }    }}