package com.aayushpuranik.todolist.domain.useCaseimport javax.inject.Injectclass ValidateName @Inject constructor() {    fun execute(name:String?): ValidateResult {        if(name.isNullOrBlank()) {            return ValidateResult(false, "Name field is required")        }        if((name?.trim()?.length ?: 0) > 0 && (name?.trim()?.length ?: 0) < 10) {            return ValidateResult(true, )        }        return ValidateResult(false, "Name field can have 0-10 characters")    }}